import React, { Component, Fragment } from 'react';
import { Link } from 'react-router-dom';
import classNames from 'classnames'
import Canvas from './Canvas';

import logo from '../../assets/img/header-logo-no-border.png';

const navigationmenu = [
    {
        id: 1,
        linkText: 'Home',
        child: true,
        submenu: [
            {
                id: 11,
                link: '/',
                linkText: 'Home One'
            },
            {
                id: 12,
                link: '/hometwo',
                linkText: 'Home Two'
            },
            {
                id: 13,
                link: '/homethree',
                linkText: 'Home Three'
            },
        ]
    },
    {
        id: 2,
        linkText: 'Apartments',
        child: true,
        submenu: [
            {
                id: 21,
                link: '/apartment-details',
                linkText: 'Apartments Details'
            },
            {
                id: 22,
                link: '/trending',
                linkText: 'Apartments List 2'
            },
            {
                id: 23,
                link: '/apartment-grid',
                linkText: 'Apartments Grid'
            },
            {
                id: 24,
                link: '/apartment-list',
                linkText: 'Apartments List'
            },
        ]
    },
    {
        id: 3,
        linkText: 'News',
        child: true,
        submenu: [
            {
                id: 31,
                link: '/news',
                linkText: 'News'
            },
            {
                id: 32,
                link: '/news-details',
                linkText: 'News Details'
            },
        ]
    },
    {
        id: 4,
        linkText: 'Pages',
        child: true,
        submenu: [
            {
                id: 41,
                child: true,
                linkText: 'Places',
                submenu: [
                    {
                        id: 411,
                        link: '/places',
                        linkText: 'Places Near By'
                    },
                    {
                        id: 412,
                        link: '/places-details',
                        linkText: 'Places Details'
                    },
                ]
            },
            {
                id: 42,
                link: '/classification',
                linkText: 'Classification'
            },
            {
                id: 43,
                link: '/gallery',
                linkText: 'Our Gallery'
            },
            {
                id: 44,
                link: '/contact',
                linkText: 'Contact Us'
            },
            {
                id: 45,
                link: '/about',
                linkText: 'About Us'
            },
            {
                id: 46,
                link: '/broker',
                linkText: 'Brokers'
            },
        ]
    },
    {
        id: 5,
        linkText: 'Contact Us',
        link: '/contact'
    },
]
class Headerthree extends Component {
    constructor(props) {
        super(props);
        this.state = {
            classmethod: false,
            togglemethod: false,
        };
        this.addClass = this.addClass.bind(this);
        this.removeClass = this.removeClass.bind(this);
        this.toggleClass = this.toggleClass.bind(this);
    }
    addClass() {
        this.setState({
            classmethod: true
        });
    }

    removeClass() {
        this.setState({
            classmethod: false
        });
    }
    toggleClass() {
        this.setState({
            togglemethod: !this.state.togglemethod
        });
    }
    componentDidMount() {
        window.addEventListener('resize', () => {
            this.setState({
                isMobile: window.innerWidth < 991
            });
        }, false);
        window.addEventListener('load', () => {
            this.setState({
                isMobile: window.innerWidth < 991
            });
        }, false);

    }
    getNextSibling = function (elem, selector) {
        // Get the next sibling element
        var sibling = elem.nextElementSibling;
        // If there's no selector, return the first sibling
        if (!selector) return sibling;
        // If the sibling matches our selector, use it
        // If not, jump to the next sibling and continue the loop
        while (sibling) {
            if (sibling.matches(selector)) return sibling;
            sibling = sibling.nextElementSibling
        }
    }
    triggerChild = (e) => {
        let subMenu = '';
        subMenu = (this.getNextSibling(e.target, '.submenu') !== undefined) ? this.getNextSibling(e.target, '.submenu') : null;
        if (subMenu !== null && subMenu !== undefined && subMenu !== '') {
            subMenu.classList = subMenu.classList.contains('d-block') ? 'submenu' : 'submenu d-block';
        }
    }
    render() {
        const className = this.state.isMobile ? 'breakpoint-on' : '';
        const classNamess = this.state.isMobile ? 'd-none' : '';
        return (
            <Fragment>
                <header className="header-three">
                    <div className="header-top">
                        <div className="container container-custom-three">
                            <div className="d-md-flex align-items-center justify-content-between">
                                <p className="welcome-text">We Make You Feel Comfortable</p>
                                <ul className="header-top-info">
                                    <li>
                                        <i className="fal fa-Clock"> </i>
            Mon - Sat 9.00 - 18.00
          </li>
                                    <li>
                                        <i className="fal fa-Clock"> </i>
            Mon - Sat 9.00 - 18.00
          </li>
                                </ul>
                            </div>
                        </div>
                    </div>
                    <div className="main-menu-area sticky-header">
                        <div className="container container-custom-three">
                            <div className={`nav-container d-flex align-items-center justify-content-between ${className}`}>
                                {/* Site Logo */}
                                <div className="site-logo">
                                    <Link to="#"><img src={logo} alt="" /></Link>
                                </div>
                                {/* Main Menu */}
                                <div className={classNames("nav-menu d-lg-flex align-items-center justify-content-between", { "menu-on": this.state.togglemethod })}>
                                    {/* Navbar Close Icon */}
                                    <div className="navbar-close" onClick={this.toggleClass}>
                                        <div className="cross-wrap"><span className="top" /><span className="bottom" /></div>
                                    </div>
                                    {/* Mneu Items */}
                                    <div className="menu-items">
                                    <ul>
                                        {navigationmenu.length > 0 ? navigationmenu.map((item, i) => (
                                            <li key={i} className={`menu-item ${item.child ? 'menu-item-has-children' : ''} `} onClick={this.triggerChild}>
                                                {item.child ? <Link onClick={e => e.preventDefault()} to="/"> {item.linkText} </Link> : <Link to={item.link}> {item.linkText} </Link>}
                                                {item.child ?
                                                    <ul className="submenu" role="menu">
                                                        {item.submenu.map((sub_item, i) => (
                                                            <li key={i} className={`menu-item ${sub_item.child ? 'menu-item-has-children' : ''} `}>
                                                                {sub_item.child ? <Link onClick={e => e.preventDefault()} to="/"> {sub_item.linkText} </Link> : <Link to={sub_item.link}> {sub_item.linkText} </Link>}
                                                                {sub_item.submenu ?
                                                                    <ul className="submenu">
                                                                        {sub_item.submenu.map((third_item, i) => (
                                                                            <li key={i}><Link
                                                                                to={third_item.link}>{third_item.linkText}</Link>
                                                                            </li>
                                                                        ))}
                                                                    </ul> : null}
                                                            </li>
                                                        ))}
                                                    </ul>
                                                    : null
                                                }
                                            </li>
                                        )) : null}
                                    </ul>
                                    </div>
                                </div>
                                {/* navbar right content */}
                                <div className="menu-right-buttons">
                                    {/* Log in icon */}
                                    <div className="login-btn">
                                        <Link to="#" id="loginBtn"><i className="fal fa-user" /></Link>
                                    </div>
                                    {/* search btton */}
                                    <div className="search">
                                        <Link to="#" className="search-icon" id="searchBtn">
                                            <i className="fal fa-search open-icon" />
                                            <i className="fal fa-times close-icon" />
                                        </Link>
                                        <div className="search-form">
                                            <form action="#">
                                                <input type="text" placeholder="Search your keyword..." />
                                                <button type="submit"><i className="far fa-search" /></button>
                                            </form>
                                        </div>
                                    </div>
                                    {/* Off canvas Toggle */}
                                    <div className="toggle">
                                        <Link to="#" id="offCanvasBtn" onClick={this.addClass}><i className="fal fa-bars" /></Link>
                                    </div>
                                    {/* Navbar Toggler */}
                                    <div className={classNames("navbar-toggler", { "active": this.state.togglemethod })} onClick={this.toggleClass}>
                                        <span /><span /><span />
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </header>
                <div className={classNames("offcanvas-wrapper", { "show-offcanvas": this.state.classmethod })}>
                    <div className={classNames("offcanvas-overly", { "show-overly": this.state.classmethod })} onClick={this.removeClass} />
                    <div className="offcanvas-widget">
                        <Link to="#" className="offcanvas-close" onClick={this.removeClass} ><i className="fal fa-times" /></Link>
                        <Canvas />
                    </div >
                </div >
            </Fragment>
        );
    }
}

export default Headerthree;
