import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import $ from 'jquery';
// import 'magnific-popup';

// import videobg from '../../../assets/img/text-block/04.jpg';
// import aboutimg from '../../../assets/img/text-block/05.jpg';
import myImage from '../../../assets/img/team/uf-logo-transparent.png'; // replace with the actual path to your image

const featurespost = [
    { icon: 'flaticon-coffee', title: 'breakfast', animation: '.3s' },
    { icon: 'flaticon-air-freight', title: 'Airport Pickup', animation: '.4s' },
    { icon: 'flaticon-marker', title: 'city guide', animation: '.5s' },
    { icon: 'flaticon-barbecue', title: 'bbq party', animation: '.6s' },
    { icon: 'flaticon-hotel', title: 'luxury room', animation: '.7s' },
];
class About extends Component {
    componentDidMount() {
        // function popup() {
        //     $('.popup-video').magnificPopup({
        //         type: 'iframe',
        //     });
        // }
        // popup();
    }
    render() {
        return (
            <section className="about-section pt-115 pb-115">
                <div className="container about-container ">
                    {/* <div className="video-wrap video-wrap-two video-about mb-60" style={{ backgroundImage: "url(" + videobg + ")" }}>
                        <Link to="http://www.youtube.com/embed/watch?v=EEJFMdfraVY" className="popup-video"><i className="fas fa-play" /></Link>
                    </div> */}
                    <div className="section-title about-title text-center">
                        <span className="title-tag">What We're <span>About</span></span>
                        <h2>A Smarter Standard for Furnished Living</h2>
                        <br />
                        
                        <p>At Urban Fox Rentals, we believe housing should work for you—not the other way around. Our apartments are designed for people who want to focus on their work, their goals, and their lives—without being burdened by high costs, complicated leases, or missing essentials.</p>
                        <br />
                        

                        <p>Located in Park Slope, Brooklyn, each unit is fully furnished and move-in ready. High-speed internet, in-unit washer/dryer, a full entertainment setup with smart TV and soundbar, a complete kitchen, and workspace with ergonomic seating—it's all here. Even the cleaning supplies are stocked.</p>
                        <br />
                        <br />


                        <p>Privacy and quality were top priorities when we built this experience. Each space feels like a true apartment—not a shared room or temporary setup. You’ll have room to host, relax, or simply enjoy quiet on your own terms.</p>
                        <br />
                        
                       <p>We created Urban Fox Rentals for professionals, students, and creatives who want a well-equipped, dependable living space that’s both comfortable and equitable. No hidden fees. No luxury markup. Just thoughtful design, flexible options, and everything you need to feel at home from day one.</p>
                        <br />
                        <br />
                        <img src={myImage} alt="Urban Fox Logo "/> {/* Add this line */}
                        


                    </div>
                    {/* <ul className="about-features">
                        {featurespost.map((item, i) => (
                            <li key={i} className="wow fadeInUp" data-wow-delay={item.animation}>
                                <Link to="#">
                                    <i className={item.icon} />
                                    <i className={"hover-icon " + item.icon + ""} />
                                    <span className="title">{item.title}</span>
                                </Link>
                            </li>
                        ))}
                    </ul> */}
                    {/* <div className="about-text-box">
                        <div className="row align-items-center">
                            <div className="col-lg-6">
                                <div className="about-img">
                                    <img src={aboutimg} alt="" />
                                </div>
                            </div>
                            <div className="col-lg-6">
                                <div className="about-text">
                                    <span>Restaurant</span>
                                    <h3>Get Restaurant Facilities &amp; Many Other More</h3>
                                    <p>
                                        Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tem por
                                        incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud
                                        exercitation ullamco laboris nisi ut aliquip.
            </p>
                                    <Link to="/contact" className="main-btn btn-filled">take a tour</Link>
                                </div>
                            </div>
                        </div>
                    </div> */}
                </div>
            </section>
        );
    }
}

export default About;
